            .then(data => {
                const newEthereumPrice = data.ethereum.usd;
                document.getElementById('ethereumPrice').innerText = `$${newEthereumPrice}`;
            })
            .catch(error => console.error('Error:', error));
    }

    // Function to update Tether price
    function updateTetherPrice() {
        fetch('https://api.coingecko.com/api/v3/simple/price?ids=tether&vs_currencies=usd')
            .then(response => response.json())
            .then(data => {
                const newTetherPrice = data.tether.usd;
                document.getElementById('tetherPrice').innerText = `$${newTetherPrice}`;
            })
            .catch(error => console.error('Error:', error));
    }

    // Update prices when the window is loaded
    window.onload = function() {
        updateBitcoinPrice();
        updateEthereumPrice();
        updateTetherPrice();
    };
</script>

