<script>
    // Create an audio element for the sound
    const audio = document.createElement("audio");
    audio.src = "https://livechat.falconinvest.biz/livechat.mp3"; // Replace with the URL of your sound file
    audio.style.display = "none"; // Hide the audio element

    // Play the sound when the popup appears
    function playSound() {
        audio.play();
    }

    // Create a chat button element
    const chatButton = document.createElement("div");
    chatButton.style.position = "fixed";
    chatButton.style.bottom = "20px";
    chatButton.style.right = "20px";
    chatButton.style.zIndex = "9999";

    // Create an anchor element for the link
    const chatLink = document.createElement("a");
    chatLink.href = "https://livechat.CAGLQLlWV481.biz";
    chatLink.target = "_blank";

    // Create an image element for the button
    const chatImage = document.createElement("img");
    chatImage.src = "https://livechat.CAGLQLlWV481.biz/chatbot.png"; // Replace with your image URL
    chatImage.alt = "Live Chat";

    // Apply styles to the chat image
    chatImage.style.width = "60px"; // Adjust the size as needed
    chatImage.style.height = "60px"; // Adjust the size as needed
    chatImage.style.cursor = "pointer";

    // Append the image element to the anchor element
    chatLink.appendChild(chatImage);

    // Append the anchor element to the chat button element
    chatButton.appendChild(chatLink);

    // Append the chat button element to the body
    document.body.appendChild(chatButton);

    // Create a chat popup text element
    const chatPopupText = document.createElement("div");
    chatPopupText.style.position = "fixed";
    chatPopupText.style.bottom = "80px";
    chatPopupText.style.right = "70px"; // Adjust the position as needed
    chatPopupText.style.zIndex = "9998";
    chatPopupText.style.backgroundColor = "white";
    chatPopupText.style.color = "black";
    chatPopupText.style.borderRadius = "10px"; // Rounded edges
    chatPopupText.style.padding = "10px";
    chatPopupText.style.display = "none";

    // Set the chat popup text content
    chatPopupText.textContent = "Hi, chat with us here.";

    // Append the chat popup text element to the body
    document.body.appendChild(chatPopupText);

    // Show the chat popup text and play the sound after 8 seconds
    setTimeout(() => {
        chatPopupText.style.display = "block";
        playSound(); // Play the sound when the popup appears
    }, 8000);

    // Hide the chat popup text after 60 seconds
    setTimeout(() => {
        chatPopupText.style.display = "none";
    }, 60000);
</script>
